curl "https://ssd.jpl.nasa.gov/api/horizons.api?format=text&COMMAND='10'&OBJ_DATA='NO'&MAKE_EPHEM='YES'&EPHEM_TYPE='OBSERVER'&CENTER='coord@399'&COORD_TYPE='GEODETIC'&REF_SYSTEM='ICRF'&APPARENT='AIRLESS'&TIME_DIGITS='MINUTES'&START_TIME='2000-01-01%2012:00'&STOP_TIME='2100-12-31%2012:00'&STEP_SIZE='1%20d'&CSV_FORMAT='NO'&QUANTITIES='34'&SITE_COORD='0,0,0'" \
    | awk '/\$\$SOE/{flag=1;next}/\$\$EOE/{flag=0}flag{split($1,a,"-");year=a[1];{if(year!=prev){prev=year;day=0}else{day++}};h=$(NF-2);m=$(NF-1);s=$NF;eot=(h+(m+s/60)/60-12)*60; printf "%d,%.1f,%.2f\n",year,day+0.5,eot}' > eot.csv
